import axios from "axios"
import { useEffect, useState } from "react"
import { useParams } from "react-router-dom"
import swal from 'sweetalert';
export function Detail(){
    const [singleItem,setSingleItem]=useState(null)
    const {id}=useParams()
    const [danger,setDanger]=useState(null)
    const [title,setTitle]=useState("");
    const [content,setContent]=useState("");
    const [cataogry,setCataogry]=useState("");
    const [auother,setAuother]=useState("");
    const [image,setImage]=useState("");
   
    
        // ?????????????????????

    const getSingleItem=async()=>{
        
        await axios('http://localhost:8000/api/v1/post/'+id)
        .then(function (response) {
            setSingleItem(response.data.getSinglePost)
            console.log(response.data.getSinglePost);
          })
          .catch(function (error) {
            console.log(error);
          });
    }

    useEffect(()=>{
getSingleItem()
    },[])

    if(!singleItem){
        return<p>loding...</p>
    }
    const formattedDate = Intl.DateTimeFormat('en-US', {
        month: 'long',
        day: "numeric",
        year: "numeric"
    }).format(new Date(singleItem.createdAt))

    const onDanger=()=>{
        setDanger(1)
    }

    const cancel=()=>{
        setDanger(null)
    }


    // ?????????????????????

   

        const submit=async()=>{
          if(title.trim() && content.trim() && cataogry.trim() && auother.trim() && image.trim() )
            {await axios.post('http://localhost:8000/api/v1/post', {
                title: title,
                content: content,
                category:cataogry,
                author:auother,
                image:image
              })
              .then(function (response) {
                // alert("successfully posted")
                swal("success", "successfully posted", "success");
                setDanger(null)

              })
              .catch(function (error) {
                console.log(error);
              })};

            if(!title){
                setTitle(1)
            }
            if(!content){
                setContent(1)
            }
            if(!cataogry){
                setCataogry(1)
            }
            if(!auother){
                setAuother(1)
            }
            if(!image){
                setImage(1)
            }

            

        }

   
    return<>
    
    <main class="container my-4">
        <div class="row">
            <article class="col-lg-8">
                <h2 class="blog-post-title">{singleItem.title}</h2>
                <p class="blog-post-meta">{formattedDate}<a href="#">{singleItem.author}</a></p>

                <img class="mb-3 img-fluid" src={singleItem.image} alt=""/>
                
                <div class="blog-post-content">
                    <p>{singleItem.content}</p>
                   
                </div>
            </article>

            <aside class="col-lg-4">
                <div class="p-4 bg-light">
                    <h3 class="mb-4">Related Posts</h3>

                    <div class="mb-4">
                        <div class="row align-items-center">
                            <div class="col-auto">
                                <img src="https://via.placeholder.com/100" class="img-fluid rounded" alt="Related Post Image"/>
                            </div>
                            <div class="col">
                                <h4><a href="#" class="text-decoration-none">Related Post Title 1</a></h4>
                                <p>Short description or excerpt of the related post. Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
                            </div>
                        </div>
                    </div>

                    <div class="mb-4">
                        <div class="row align-items-center">
                            <div class="col-auto">
                                <img src="https://via.placeholder.com/100" class="img-fluid rounded" alt="Related Post Image"/>
                            </div>
                            <div class="col">
                                <h4><a href="#" class="text-decoration-none">Related Post Title 2</a></h4>
                                <p>Short description or excerpt of the related post. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.</p>
                            </div>
                        </div>
                    </div>
                </div>
            </aside>
        </div>
    </main>
    {/* ............. */}
    {!danger && <button onClick={onDanger} className="btn bg-danger text-light">submit</button>}
    {/* lllllllll */}
   {danger && <div className="bg-black d-flex align-items-center justify-content-center" style={{height:"500px",opacity:"20"}}>
    <div className="bg-warning rounded-4 d-flex align-items-center" style={{minHeight:"300px",width:"300px",padding:"20px"}}>
        <div>
            <input type="text" onChange={(e)=>setTitle(e.target.value)} placeholder="title" style={{width:"250px",height:"40px",border:"none",borderRadius:"5px",outline:"none"}} className="my-2"/>
            {/* ......... */}
            {title==1 && <span className="text-danger">*title is not required</span>}
             {/* ......... */}
            <input type="text" onChange={(e)=>setContent(e.target.value)} placeholder="content" style={{width:"250px",height:"40px",border:"none",borderRadius:"5px",outline:"none"}} className="my-2"/>
              {/* ......... */}
              {content==1 && <span className="text-danger">*content is not required</span>}
             {/* ......... */}
            <input type="text" onChange={(e)=>setCataogry(e.target.value)} placeholder="cataogry" style={{width:"250px",height:"40px",border:"none",borderRadius:"5px",outline:"none"}} className="my-2"/>
              {/* ......... */}
              {cataogry==1 && <span className="text-danger">*cataogry is not required</span>}
             {/* ......... */}
            <input type="text" onChange={(e)=>setAuother(e.target.value)} placeholder="auother" style={{width:"250px",height:"40px",border:"none",borderRadius:"5px",outline:"none"}} className="my-2"/>
              {/* ......... */}
              {auother==1 && <span className="text-danger">*auother is not required</span>}
             {/* ......... */}
            <input type="text" onChange={(e)=>setImage(e.target.value)} placeholder="image" style={{width:"250px",height:"40px",border:"none",borderRadius:"5px",outline:"none"}} className="my-2"/>
              {/* ......... */}
              {image==1 && <span className="text-danger">*image is not required</span>}
             {/* ......... */}
            <div className="d-flex justify-content-between mt-3">
            <button onClick={submit} className="btn bg-success text-light">Submit</button>
                <button onClick={cancel} className="btn bg-danger text-light">cancel</button>
            </div>
            </div>
            
       

    </div>
    </div>}
      {/* lllllllll */}
    </>
}
